import { Button, List, ListItem, ListItemIcon, ListItemText } from '@material-ui/core'
import Drawer from '@material-ui/core/Drawer'
import Typography from '@material-ui/core/Typography'
import { AppsSharp, CodeRounded, DashboardSharp, FolderOpenSharp, SettingsSharp } from '@material-ui/icons'
import {makeStyles} from '@material-ui/core'

const drawerWidth = 240
const useStyles = makeStyles((theme) =>{
    return{
        root:{
            background:"#F4F4F4"
        },
        nav:{
            width: drawerWidth,
            background: 'red'
        },
        title:{
            padding: theme.spacing(2)
        },
        list:{
            padding: theme.spacing(2)
        },
        menuItem: {
            display : 'flex'
        },
        button : {
            margin: theme.spacing(2),
            padding : theme.spacing(1)
        }
}})

const Layout = ({children}) => {
    const menuItems = [
        {
            text: 'DashBoard',
            icon:<DashboardSharp color="primary" />,
            path:'/'
        },
        {
            text: 'Create',
            icon:<AppsSharp color="primary" />,
            path:'/create'
        },
        {
            text: 'Manage',
            icon:<FolderOpenSharp color="primary" />,
            path:'/manage'
        },
        {
            text: 'Settings',
            icon:<SettingsSharp color="primary" />,
            path:'/settings'
        },
    ]
    const classes = useStyles();
    
    return ( 

        <div className={classes.root}>

            {/** toolbar */}


            {/**menu droite */}
            <Drawer
            variant = "permanent"
            anchor = 'left'
            className = {classes.nav}

            >
                <div>
                    <div>
                        <div>
                            <CodeRounded color = "primary" />
                            <Typography 
                            variant="h5" 
                            className={classes.title}
                            color="primary"
                            >
                                Transformers 

                            </Typography>

                        </div>
                        
                        <Button 
                        variant='contained' 
                        color='primary' 
                        className = {classes.button}
                        >
                            Générer le script
                        </Button>
                    </div>

                    {/**Les liens du menu */}
                    <List className={classes.list}>
                        {menuItems.map(item => (
                            <ListItem button className={classes.menuItem}>
                                <ListItemIcon>{item.icon}</ListItemIcon>
                                <ListItemText primary = {item.text} />
                            </ListItem>

                        ))}
                    </List>
                </div>
            </Drawer>


            {/**future page */}
            {children}
        </div>
     );
}
 
export default Layout;
