import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, IconButton, InputLabel, Menu, MenuItem, Paper, Select, TextField } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Comment, Delete, FileCopy, Http, MoreVert, Web } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1)

    },
    fields:{
        minWidth:300,
        marginRight:theme.spacing(2)
    },
    dropDown:{
        minWidth:100,
    }
}))
const WebAction = ({actionID,index}) => {
    const classes = useStyles()
    const [anchorEl, setAnchor] = useState(null)
    const { deleteAction, duplicateAction,saveData} = useContext(ActionContext)
    const [state, setState] = useState([])
    const [urlState, setUrlState] = useState("Authentification")
    const [openDialog, setOpenDialog] = useState(false)

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }
    const reduire = () =>{

        setState({ ...state, id:actionID, index:index})

        //console.log(state,actionID)
    }

    const testConnection = async (e) =>{
        if(e.target.value !== ''){

            console.log('accessing ',e.target.value)

            await fetch(`http://localhost:5000/api/PARPRE/link?url=${e.target.value}`)
                .then(res => res.json())
                .then(result => console.log(result.result))
        }
  
    }
    const showDialog = () =>{
        setOpenDialog(true);
    }

    const closeDialog = () =>{
        setOpenDialog(false);
    }


    return ( 
        <div>
            <Paper 
                elevation={0}
                className= {classes.root}>
                    <Box 
                display='flex'
                >
                    <Box my="auto" mx={2} >
                        <Web color="primary" />

                    </Box>
                    <Box my="auto" flexGrow={0.5}>
                        <TextField
                        
                        className={classes.fields} 
                        id='url'
                        
                        color='primary'
                        label= 'Lien'
            
                        onChange={(e) => setState({...state, link:e.target.value})}
                        onBlur={(e) => (testConnection(e))}
                        />
                    </Box>
                    <Box flexGrow={0.6} >
                    <FormControl className={classes.dropDown}>
                            <InputLabel>Etat</InputLabel>
                            <Select value={urlState} onChange={(e) => (setUrlState(e.target.value))}>
                                <MenuItem value="Authentification" >Autentification</MenuItem>
                                <MenuItem value="Navigation">Navigation</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>
                    
                    <Box my="auto">
                        <FormControl className={classes.dropDown}>
                            <Button variant="contained" color="primary" onClick={showDialog}>options</Button>
                        </FormControl>
                    </Box>
                    <Dialog
                    open={openDialog}
                    onClose={closeDialog}
                    fullWidth={200}
                    >
                        <DialogTitle id='dialog-title' >Informations </DialogTitle>
                        <DialogContent>
                            <Box display="flex" justifyContent="space-between" my="auto">
                                <TextField
                                autoFocus
                                margin="dense"
                                id="login"
                                label="email"
                                type="email"
                                
                                />
                                <TextField
                                autoFocus
                                margin="dense"
                                id="password"
                                label="mot de passe"
                                type="password"
                                
                                />
                            </Box>

                        </DialogContent>
                        <DialogActions>
                            <Button>
                                Confirmer
                            </Button>
                            
                        </DialogActions>

                    </Dialog>
                    
                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(actionID)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={() => {saveData(
                            {
                                index:index, type:"lien", url : state.link, status : urlState
                            })
                            ;setAnchor(null)}} ><FileCopy /> save</MenuItem>
                        <MenuItem  ><Comment /> Réduire</MenuItem>
                    </Menu>
                    <IconButton onClick={handleClick}>
                        <MoreVert />

                    </IconButton>
                </Box>
            </Paper>
        </div>
     );
}
 
export default WebAction;
