import React from 'react'

import { AppBar, Button, List, ListItem, ListItemIcon, ListItemText, Toolbar } from '@material-ui/core'
import Drawer from '@material-ui/core/Drawer'
import Typography from '@material-ui/core/Typography'
import { AppsSharp, CodeRounded, DashboardSharp, FolderOpenSharp, SettingsSharp } from '@material-ui/icons'
import {makeStyles} from '@material-ui/core'
import { useHistory, useLocation } from 'react-router'

const drawerWidth = 240
const useStyles = makeStyles((theme) =>{
    return{
        root:{
            display:'flex',
            background: '#F6F6FB'
        },
        nav:{
            width: drawerWidth,
        },
        title:{
            padding: theme.spacing(2)
        },
        list:{
            padding: theme.spacing(2)
        },
        menuItem: {
            display : 'flex'
        },
        button : {
            margin: theme.spacing(2),
            padding : theme.spacing(1)
        },
        logo : {
            display : 'flex'
        },
        active:{
            background:'#F2F2F2'
        },
        appbar:{
            width:`calc(100% - ${drawerWidth}px)`
        },
        toolbar:theme.mixins.toolbar,
        page: {
            background:'F9F9F9',
            width:'100%',
            padding: theme.spacing(3)
        },
        drawerPaper:{
            width : drawerWidth,
            backgroundColor:"#FFFFF",
            borderRight:'0px'

        }

}})

const Layout = ({children}) => {
    const menuItems = [
        {
            text: 'DashBoard',
            icon:<DashboardSharp color="primary" />,
            path:'/'
        },
        {
            text: 'Create',
            icon:<AppsSharp color="primary" />,
            path:'/create'
        },
        {
            text: 'Manage',
            icon:<FolderOpenSharp color="primary" />,
            path:'/manage'
        },
        {
            text: 'Settings',
            icon:<SettingsSharp color="primary" />,
            path:'/settings'
        },
    ]
    const classes = useStyles();
    const history = useHistory();
    const location = useLocation();
    
    return ( 

        <div className={classes.root}>

            {/** toolbar */}
            <AppBar className={classes.appbar} elevation={0}>
                <Toolbar className={classes.toolbar}>
                    <Typography>Menu</Typography>
                </Toolbar>
            </AppBar>

            {/**menu droite */}
            <Drawer
            elevation={0}
            variant = "permanent"
            anchor = 'left'
            className = {classes.nav}
            classes = {{paper : classes.drawerPaper}}


            >
                <div>
                    <div>
                        <div className={classes.logo}>
                            <Typography 
                            variant="h5" 
                            className={classes.title}
                            color="primary"
                            >
                                Transformers 

                            </Typography>

                        </div>
                        
                        <Button 
                        variant='contained' 
                        color='primary' 
                        className = {classes.button}
                        >
                            Générer le script
                        </Button>
                    </div>

                    {/**Les liens du menu */}
                    <List>
                        {menuItems.map(item => (
                            <ListItem 
                                key = {item.text}
                                button
                                onClick= { () => history.push(item.path)}
                                className = {location.pathname === item.path ? classes.active : null}
                            >
                                <ListItemIcon>{item.icon}</ListItemIcon>
                                <ListItemText primary = {item.text} />
                            </ListItem>    
                        ))}
                    </List>
                </div>
            </Drawer>


            {/**future page */}
            <div className={classes.page} >
                <div className={classes.toolbar}>

                </div>
                {children}

            </div>
        </div>
     );
}
 
export default Layout;
