import { Box, FormControl, IconButton, InputLabel, Menu, MenuItem, Paper, Select, TextField } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Comment, Delete, FileCopy, Http, MoreVert, SettingsSharp } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1)

    },
    fields:{

        marginRight:theme.spacing(2)
    }
}))
const Link = ({actionID,index}) => {
    const classes = useStyles()
    const [anchorEl, setAnchor] = useState(null)
    const { deleteAction, duplicateAction,saveData} = useContext(ActionContext)
    const [action, setAction] = useState('STOP')
    const [state, setState] = useState([])
    const [pingState, setPingState] = useState('ok')

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }
    const reduire = () =>{

        setState({ ...state, id:actionID, index:index})

        //console.log(state,actionID)
    }

    const testPing = async (e,server) =>{
        var error=''
        
        await fetch(`http://localhost:5000/api/PARPRE?server=${server}`)
            .then(res => res.json())
            .then(result => {setPingState(result); console.log(result.result); error= result}
        )
        
  
    }


    return ( 
        <div>
            <Paper 
                elevation={0}
                className= {classes.root}>
                    <Box 
                display='flex'
                >
                    <Box my="auto" mx={2} >
                        <Http color="primary" />

                    </Box>
                    <Box my="auto" flexGrow={1}>
                        <TextField
                        
                        className={classes.fields} 
                        id='url'
                        
                        color='primary'
                        label= 'Lien'
            
                        onChange={(e) => setState({...state, server:e.target.value})}
                        //onBlur={(e) => (testPing(e,e.target.value))}
                        />

                        <FormControl>
                            <InputLabel>Etat</InputLabel>
                            <Select>
                                <MenuItem value="Down">Down</MenuItem>
                                <MenuItem value="UP">UP</MenuItem>
                            </Select>
                        </FormControl>
                        
                    </Box>
                    

                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(actionID)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={() => {saveData(
                            {
                                index:index, type:"service", server : state.server , name : state.service, login : state.login
                            })
                            ;setAnchor(null)}} ><FileCopy /> save</MenuItem>
                        <MenuItem  ><Comment /> RÃ©duire</MenuItem>
                    </Menu>
                    <IconButton onClick={handleClick}>
                        <MoreVert />

                    </IconButton>
                </Box>
            </Paper>
        </div>
     );
}
 
export default Link;
