import {Avatar, Button, makeStyles, Paper, Typography} from '@material-ui/core'
import { useState } from 'react';
import { CallMissedSharp, Code, Description, Http, SettingsSharp, Storage, Web } from '@material-ui/icons';

const useStyles = makeStyles((theme)=>({
    root:{
        

    },
    fields:{
        padding:theme.spacing(2),
        margin:theme.spacing(2),
        display:'flex',
        justifyContent:'space-around'
    },
    
}))

const Manage = () => {

    const [POS, SetPOS] = useState([])

    const classes = useStyles()

    const iconsAction = [
        {icon : <Http />, name: 'Url'}, 
        {icon : <Web />, name: 'Action Web'}, 
        {icon : <SettingsSharp />, name: 'Service'},
        {icon : <Code />, name: 'Commande'},
        {icon : <Description />, name: 'Script'},
        {icon : <Storage />, name: 'Base de données'}
        
    ]

    const getPos = async () =>{
        console.log("asking pos")
        await fetch(`http://localhost:5000/api/POS`)
            .then(Response =>Response.json())
            .then(result=>{
                console.log(result)
                SetPOS(result)
            })
            .then(()=>console.log(POS))
            

            

    }
    return ( 
        <div>
            <Typography>Toutes les Procedures d'Ouverture de Service (POS)</Typography>
            <Button color="primary" onClick={()=>getPos()}>
                SSA
            </Button>
            
            <div>
                {POS.map(pos => (
                    <Paper className= {classes.fields} elevation={0.5} >
                        <Avatar>{pos.name[0]}</Avatar>
                        <Typography> {pos.name}</Typography>
                        <Typography> {pos.Arret.length}</Typography>
                        <Typography> {pos.auteur}</Typography>
                        <Typography> {pos.date_de_creation}</Typography>
                        <Typography> {pos._id}</Typography>
                    </Paper>
                    
                ))}
            </div>
            
        </div>
     );
}
 
export default Manage;
