import {Avatar, Button, Card, CardContent, Grid, makeStyles, Paper, Typography} from '@material-ui/core'
import { useEffect, useState } from 'react';
import { CallMissedSharp, Code, Description, Http, SettingsSharp, Storage, Web } from '@material-ui/icons';
import ListSSA from '../Components/Manage/ListSSA';

const useStyles = makeStyles((theme)=>({
    root:{
        margin:theme.spacing(1),
        flexGrow:theme.spacing(1)

    },
    fields:{
        padding:theme.spacing(2),
        display:'flex',
        justifyContent:'space-between'
    },
    
}))


const Manage = () => {
    
    const [POS, SetPOS] = useState([])

    useEffect(()=>{
        getPos()
    },POS)


    const classes = useStyles()

    const getPos = async () =>{
        await fetch(`http://localhost:5000/api/POS`)
            .then(Response =>Response.json())
            .then(result=>{
                console.log(result)
                SetPOS(result)
            })
            
    }
    return ( 
        <div>
            <Typography>Toutes les Procedures d'Ouverture de Service (POS)</Typography>
                        
            <div>
                <ListSSA ssa={{name:"test"}}/>

                {POS.map(pos => (                                      
                    <ListSSA ssa={pos} key={pos._id}/>
                ))}
            </div>
            
        </div>
     );
}
 
export default Manage;
