import { Avatar, Box, Chip, FormControl, Grid, IconButton, InputLabel, Menu, MenuItem, Paper, Select, TextField, Typography } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Comment, Delete, FileCopy, MoreVert, SettingsSharp } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(1),
        marginBottom: theme.spacing(1)

    },
    fields:{
        //marginRight:theme.spacing(2),
        //minWidth:theme.spacing(12),
        
    },
    smallAvatar: {
      width: theme.spacing(2.5),
      height: theme.spacing(2.5)
    }
}))
const Service = ({index,type}) => {
    //css
    const classes = useStyles()

    const [anchorEl, setAnchor] = useState(null)
    const [action, setAction] = useState('STOP')
    //létat du composant
    const [state, setState] = useState([])
    //l'état du server
    const [pingState, setPingState] = useState("ko")
    const [status, setStatus] = useState(type)
    const [OS, SetOS] = useState("")


    //context pour sauvegarder l'état dans le parent
    const { deleteAction, duplicateAction,saveData,test} = useContext(ActionContext)

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }

    const saveInformations = () =>{
        saveData(
            {
                index:index, type:"service", server : state.server , name : state.service, login : state.login
            }
        )
    }
    //test si le service existe
    const testService = async(service)=>{

        if(state.server === null || pingState === "ko"){return}

        console.log("Testing service : ",service,"server ",state.server)
        fetch(`http://localhost:5000/api/PARPRE/service?name=${service}&server=${state.server}`)
            .then(res => res.json())
            .then(result => console.log(result))
            .then(() => saveInformations())
    }
    //Fait un ping
    const testPing = async (server) =>{
        
        
        await fetch(`http://localhost:5000/api/PARPRE?server=${server}`)
            .then(res => res.json())
            .then(result => {
                setPingState(result)
                console.log(result.state)
                if(result.state === "ok"){
                    saveInformations()
                } 
            
            }
        )
    }


    return ( 
        <div>
            <Paper 
                elevation={0}
                className= {classes.root}>
                <Grid 
                    container
                    spacing={2}
                    alignItems="center"
                >
                    <Grid item md={1} xl={1} my="auto" item >
                        <SettingsSharp color="primary" />
                    </Grid>
                    <Grid item md={2} xl={2}>
                        <TextField                        
                        className={classes.fields} 
                        id='server'                        
                        color='primary'
                        label= 'Serveur'            
                        onChange={(e) => setState({...state, server:e.target.value})}
                        onBlur={(e) => (testPing(e.target.value))
                        }
                        />
                    </Grid>
                    <Grid item md={2} xl={2}>
                    <FormControl className={classes.fields}>
                        <InputLabel>Action</InputLabel>
                        <Select fullWidth value={status} onChange={(e) => setStatus(e.target.value)} >
                            <MenuItem value="stop">Stop</MenuItem>
                            <MenuItem value="start">Start</MenuItem>
                            <MenuItem value="status" >Status</MenuItem>                            
                        </Select>
                    </FormControl>
                    </Grid>
                    <Grid item md={3} xl={2}>
                        <TextField 
                        autoComplete="false"
                        className={classes.fields} 
                        id='serviceName'
                        color='primary'
                        label= 'Service'
                        onChange={(e) => setState({...state, service:e.target.value})}
                        onBlur={(e) => testService(e.target.value) }

                        />
                    </Grid>
                    
                    <Grid item md={3} xl={1}>
                        <TextField 
                            className={classes.fields} 

                            id='login'
                            color='primary'
                            label= 'Login' 
                            onChange={(e) => setState({...state, login:e.target.value})}
                            onBlur={() => saveInformations()}

                        />
                    </Grid>


                    
                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => {

                            deleteAction(index)
                            setAnchor(null)
                            
                            }} >
                            
                            <Delete />Supprimer
                        
                        </MenuItem>
                        <MenuItem onClick={() => {saveData(
                            {
                                index:index, type:"service", server : state.server , name : state.service, login : state.login
                            })
                            ;setAnchor(null)}} ><FileCopy /> save</MenuItem>
                        <MenuItem onclick={()=> test()} ><Comment /> Réduire</MenuItem>
                    </Menu>

                    
                    <Grid item md={1} xl={1}  >
                        <Grid container spacing={0} alignItems="center">
                            <Grid item md={6} >
                            <IconButton onClick={handleClick}>
                                <MoreVert />
                            </IconButton>
                        </Grid>
                        
                        <Grid item md={6}>
                            <Box my="auto" >
                            <Avatar className={classes.smallAvatar} >
                            
                            <Typography>
                                {index + 1}
                            </Typography> 
                            
                            
                            </Avatar>

                        </Box>
                        </Grid>
                        
                    </Grid>
                        </Grid>
                        
                    
                    
                </Grid>
            </Paper>
        </div>
     );
}
 
export default Service;
