import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, Menu, MenuItem } from "@material-ui/core";
import {  Comment, Delete, FileCopy, MoreVert } from "@material-ui/icons";
import { useState } from "react";

const OptionMenu = ({index,deleteAction,duplicateAction}) => {

    const [anchorEl, setAnchor] = useState(null)

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }

    const handleClose= () => {
        setAnchor(null)
    }
    const [openDialog, setOpenDialog] = useState(false)
    
    const showDialog = () =>{
        setOpenDialog(true);
    }

    const closeDialog = () =>{
        setOpenDialog(false);
    }
    

    return ( 
        <div>
            <IconButton onClick={handleClick}>
                <MoreVert />
            </IconButton>
            <Menu
                id="menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
            >

                <MenuItem onClick={() => {

                    deleteAction(index)
                    setAnchor(null)
                    
                    }} >
                    
                    <Delete />Supprimer
                
                </MenuItem>
                <MenuItem onClick={() => {
                        
                        handleClose()
                        duplicateAction(index)
                    }
                } 
                >
                    <FileCopy /> Dupliquer
                        
                </MenuItem>
                <MenuItem onclick={showDialog} >
                    <Comment /> Options

                </MenuItem>
            </Menu>
            
            <Dialog
                    open={openDialog}
                    onClose={closeDialog}
                    fullWidth={200}
                    >
                        <DialogTitle id='dialog-option' >Options </DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                Le sélecteur est obtenu en faisant un click droit sur le champ à automatiser,  
                                Inspecter" puis click droit sur l'élément html et "copy selector"
                            </DialogContentText>
                            <div>
                                
                            </div>

                        </DialogContent>
                        <DialogActions>
                            <Button>
                                Confirmer
                            </Button>
                            
                        </DialogActions>

                    </Dialog>

        </div>
      );
}
 
export default OptionMenu;
