import React from 'react'

import { AppBar, Box, Button, List, ListItem, ListItemIcon, ListItemText, Toolbar } from '@material-ui/core'
import Drawer from '@material-ui/core/Drawer'
import Typography from '@material-ui/core/Typography'
import { AppsSharp, Code, CodeRounded, DashboardSharp, FolderOpenSharp, SettingsSharp } from '@material-ui/icons'
import {makeStyles} from '@material-ui/core'
import { useHistory, useLocation } from 'react-router'

const drawerWidth = 240
const useStyles = makeStyles((theme) =>{
    return{
        root:{
            display:'flex',
            background: '#F6F6FB'
        },
        nav:{
            width: drawerWidth,
        },
        title:{
            fontWeight:'bold',
        },
        list:{
            padding: theme.spacing(2)
        },
        menuItem: {
            display : 'flex'
        },
        button : {
            margin: theme.spacing(4),
            padding : theme.spacing(1)
        },
        logo : {
            display : 'flex'
        },
        active:{
            background:'#F2F2F2',
            paddingInline:theme.spacing(3)

        },
        nonActive:{
            paddingInline:theme.spacing(3)

        },
        appbar:{
            width:`calc(100% - ${drawerWidth}px)`
        },
        toolbar:theme.mixins.toolbar,
        page: {
            background:'F9F9F9',
            width:'100%',
            padding: theme.spacing(3)
        },
        drawerPaper:{
            width : drawerWidth,
            backgroundColor:"#FFFFF",
            borderRight:'0px'

        },
        drawerPaperLeft:{
            width : drawerWidth,
            backgroundColor:"#FFFFF",
            borderLeft:'0px'

        },
        navRight:{
            width:drawerWidth
        }

}})

const Layout = ({children}) => {
    const menuItems = [
        {
            text: 'DashBoard',
            icon:<DashboardSharp color="primary" />,
            path:'/'
        },
        {
            text: 'Create',
            icon:<AppsSharp color="primary" />,
            path:'/create'
        },
        {
            text: 'Manage',
            icon:<FolderOpenSharp color="primary" />,
            path:'/manage'
        },
        {
            text: 'Settings',
            icon:<SettingsSharp color="primary" />,
            path:'/settings'
        },
    ]
    const classes = useStyles();
    const history = useHistory();
    const location = useLocation();
    
    return ( 

        <div className={classes.root}>

            {/** toolbar */}
            <AppBar className={classes.appbar} elevation={0}>
                <Toolbar className={classes.toolbar}>
                    <Typography>Menu</Typography>
                </Toolbar>
            </AppBar>

            {/**menu droite */}
            <Drawer
            elevation={0}
            variant = "permanent"
            anchor = 'left'
            className = {classes.nav}
            classes = {{paper : classes.drawerPaper}}


            >
                <div>
                    <div>
                        <div className={classes.logo}>
                            <Box m={3} 
                            display='flex' 
                            fontWeight={800}
                            
                           >
                                <Box my='auto'>
                                  <Code color='primary'/>  
                                </Box>
                                <Typography 
                                variant="h6" 
                                className={classes.title}
                                color="primary"
                                
                                >
                                    Transformers 
                                </Typography>
                            </Box>


                        </div>
                        
                        <Button
                         
                        variant='contained' 
                        
                        color='primary' 
                        className = {classes.button}
                        >
                            Générer le script
                        </Button>
                    </div>

                    {/**Les liens du menu */}
                    <List>
                        <Box 
                        display='block'
                        alignItems='center'
                        justifyContent='space-around'>
                        {menuItems.map(item => (
                            <ListItem 
                                key = {item.text}
                                button
                                onClick= { () => history.push(item.path)}
                                className = {location.pathname === item.path ? classes.active : null}
                            >
                                <ListItemIcon>{item.icon}</ListItemIcon>
                                <ListItemText primary = {item.text} />
                            </ListItem>    
                        ))}
                        </Box>
                        
                    </List>
                </div>
            </Drawer>

            


            {/**future page */}
            <div className={classes.page} >
                <div className={classes.toolbar}>

                </div>
                {children}

            </div>
            
            {/**Left nav bar */}
            <Drawer
            elevation={0}
            variant = "permanent"
            anchor = 'right'
            classes = {{paper : classes.drawerPaperLeft}}
            className={classes.navRight}
            >
                
            </Drawer>
        </div>
     );
}
 
export default Layout;
