import { Box, IconButton, Menu, MenuItem, Paper, TextField } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Comment, Delete, FileCopy, MoreVert, SettingsSharp } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1)

    },
    fields:{
        marginRight:theme.spacing(2)
    }
}))
const Service = ({actionID,index}) => {
    const classes = useStyles()
    const [anchorEl, setAnchor] = useState(null)
    const { deleteAction, duplicateAction,saveData, testPing} = useContext(ActionContext)
    const [action, setAction] = useState('STOP')
    const [state, setState] = useState([])

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }
    const reduire = () =>{

        setState({ ...state, id:actionID, index:index})

        //console.log(state,actionID)
    }

    const test = () =>{

        var spawn = require("child_process").spawn,child;
        child = spawn("powershell.exe",["./Powershell/ping.ps1"]);
        child.stdout.on("data",function(data){
            console.log("Powershell Data: " + data);
        });
        child.stderr.on("data",function(data){
            console.log("Powershell Errors: " + data);
        });
        child.on("exit",function(){
            console.log("Powershell Script finished");
        });
        child.stdin.end();
    }


    return ( 
        <div>
            <Paper 
                elevation={0}
                className= {classes.root}>
                    <Box 
                display='flex'
                justifyContent='space-between'

                >
                    <Box my="auto">
                        <SettingsSharp color="primary" />

                    </Box>
                    <div>
                        <TextField
                        
                        className={classes.fields} 
                        id='server'
                        color='primary'
                        label= 'Serveur'
                        onChange={(e) => setState({...state, server:e.target.value})}
                        onBlur={(e) => (console.log(testPing(e.target.value)))
                        }
                        error 
                        helperText="Ce serveur n'existe pas"
                        />
                    </div>
                    <div>
                        <TextField 
                        className={classes.fields} 

                        id='login'
                        color='primary'
                        label= 'Login' 
                        onChange={(e) => setState({...state, login:e.target.value})}
                        onBlur={reduire}

                        />
                    </div>
                    <div>
                        <TextField 
                        autoComplete="false"
                        className={classes.fields} 
                        id='serviceName'
                        color='primary'
                        label= 'Service'
                        onChange={(e) => setState({...state, service:e.target.value})}
                        

                        />
                    </div>
                    <div>
                        <TextField 
                        className={classes.fields} 

                        id='Action'
                        color='secondary'
                        label= 'STOP'
                        placeholder='STOP'
                        disabled />
                    </div>

                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(actionID)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={() => saveData({state:state, index:index})} ><FileCopy /> save</MenuItem>
                        <MenuItem onClick={test} ><Comment onClick={test} /> RÃ©duire</MenuItem>
                    </Menu>
                    <IconButton onClick={handleClick}>
                        <MoreVert />

                    </IconButton>
                </Box>
            </Paper>
        </div>
     );
}
 
export default Service;
