import { FormControlLabel, FormGroup, Grid, makeStyles, Switch, TextField, Typography } from "@material-ui/core"
import { useState } from "react";
import ARCard from "../Components/ARCard"
import RightNav from "../Components/Creation/RightNav";
import ActionContext from "../Context/ActionContext";
const drawerWidthRight = 260

const useStyles = makeStyles((theme) =>{
    return{
        root:{
            width:`calc(100% - ${drawerWidthRight}px)`   
        }

}})

const handleTitleChange = () =>{

}
const Create = () => {

    const [titleOpen, setTitleOpen] = useState(false)
    //titre de la parpre ou de la POS
    const [title, setTitle] = useState("APPXXXX_SSA")
    //les actions d'arrêt
    const [StopActions, setStopActions] = useState([])
    //les Actions de relance
    const [StartActions, setStartActions] = useState([])
    //creation automatique de la verification
    const [autoCreate, setAutoCreate] = useState(false)
    //Si on fait la relance automatiquement
    const [autoRelance, setAutoRelance] = useState(false)
    //Soit PARPRE ou POS
    const [documentType, setDocumentType] = useState("")

    const [servers, setServers] = useState([
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" },
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" },
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" }
    ])

    const saveServer = (server) =>{
       //console.log("Saving server ", server)
        setServers([...servers,{name:server}])
    }

    const classes = useStyles()

    const saveStart = (object) =>{
        setStartActions(object)

    }

    const saveStop = (object) =>{
        
        let newServers = []

        object.map(action=> newServers=[...newServers,{name : action.server}])

        console.log("all servers ",newServers)

        setStopActions(object)

        //setServers(newServers)

    }

    
    return ( 
        

        <div className={classes.root}>   
            <Grid container>     
                <Grid item md={5}>
                { 
                    titleOpen ? 
                    (
                        <TextField 
                        placeholder="Changer le titre"
                        onBlur={() => setTitleOpen(false)}
                        margin="dense"
                        onChange={(e) => setTitle(e.target.value)}
                        autoFocus
                        value={title}
                        >
                            
                        </TextField>
                    ) 
                    : 
                    (
                        <div>
                            <Typography 
                            variant='h6'
                            gutterBottom
                            onClick={() => setTitleOpen(true)}
                            >
                                Nom : {title}

                            </Typography>
                        </div>
                    )
                }    
                </Grid>   

                <Grid item md={4}>
                <FormGroup>
                        <FormControlLabel
                            control = {<Switch onChange={()=> setAutoCreate(!autoCreate)}/>}
                            checked={autoCreate}
                            label = "Verification-Auto"
                        >
                        

                        </FormControlLabel>
                        
                    </FormGroup>

                </Grid> 

                <Grid item md={3}>
                    <FormGroup>
                        <FormControlLabel
                            control = {<Switch onChange={()=> setAutoRelance(!autoRelance)}/>}
                            checked={autoRelance}
                            label = "Relance-Auto"
                        >
                        

                        </FormControlLabel>
                        
                    </FormGroup>
                    
                </Grid>  
                
            </Grid>
            
                <ARCard 
                    name="Arrêt" 
                    type="stop" 
                    actions={StopActions} 
                    SetActions={saveStop} 
                    autoRelance = {autoCreate}
                    AddServer={saveServer}
                      
                />
                <ARCard 
                    name="Relance" 
                    type="start"
                    actions = {StartActions} 
                    SetActions = {saveStart} 
                    AddServer={saveServer} 

                />
                <RightNav ServerRow={servers}/>
        </div>
     );
}
 
export default Create;
