import { FormControlLabel, FormGroup, Grid, Switch, TextField, Typography } from "@material-ui/core"
import { useState } from "react";
import ARCard from "../Components/ARCard"
import ActionContext from "../Context/ActionContext";


const handleTitleChange = () =>{

}
const Create = () => {
    const [titleOpen, setTitleOpen] = useState(false)
    const [title, setTitle] = useState("APPXXXX_SSA")
    
    const [StopActions, setStopActions] = useState([])
    const [StartActions, setStartActions] = useState([])
    const [autoCreate, setAutoCreate] = useState(false)

    
    return ( 
        
        <div>   
            <Grid container>     
                <Grid item md={6}>
                { 
                    titleOpen ? 
                    (
                        <TextField 
                        placeholder="Changer le titre"
                        onBlur={() => setTitleOpen(false)}
                        margin="dense"
                        onChange={(e) => setTitle(e.target.value)}
                        autoFocus
                        value={title}
                        >
                            
                        </TextField>
                    ) 
                    : 
                    (
                        <div>
                            <Typography 
                            variant='h5'
                            gutterBottom
                            onClick={() => setTitleOpen(true)}
                            >
                                Nom Parpre : {title}

                            </Typography>
                        </div>
                    )
                }    
                </Grid>   

                <Grid item md={3}>
                    

                </Grid> 

                <Grid item md={3}>
                    <FormGroup>
                        <FormControlLabel
                            control = {<Switch />}
                            label = "Auto-création"
                        >

                        </FormControlLabel>
                    </FormGroup>
                    
                </Grid>  
                
            </Grid>
            
                <ARCard 
                    name="Arrêt" 
                    type="STOP" 
                    StopActions={StopActions} 
                    setStopActions={setStopActions} 
                      
                />
                <ARCard 
                    name="Relance" 
                    type="START"
                    StartActions = {StartActions} 
                    setStartActions = {setStartActions}  
                />
            
        </div>
     );
}
 
export default Create;
