import { Card, CardContent, CardHeader,  Container,  makeStyles, Typography} from '@material-ui/core'
import { Code, Description, SettingsSharp, Storage } from '@material-ui/icons';
import Service from './Actions/Service';
import Database from './Actions/Database';
import Command from './Actions/Command';
import ARScript from './Actions/ARScript';
import { useEffect, useState } from 'react';
import SpeedDial from '@material-ui/lab/SpeedDial'
import { SpeedDialAction, SpeedDialIcon } from '@material-ui/lab';

const cardWidth = 950
const useStyles = makeStyles(theme =>({
    root:{
        width : cardWidth,
        marginBottom : theme.spacing(2) 
    }
}))
const ARCard = ({name}) => {
    const classes = useStyles();
    const [actions, SetActions] = useState([])
    const [open,setOpen] = useState(false)

    useEffect(() =>{
        fetch("http://localhost:8000/Actions")
        .then(res => res.json())
        .then(data => SetActions(data))
    },[])

    const AddScript = () =>{
        const newState = [
            ...actions,{
                type:'script',
                id: actions.length + 1
            }
        ]
        SetActions(newState)
    }
    const AddDB = () =>{
        const newState = [
            ...actions,{
                type:'database',
                id: actions.length + 1
            }
        ]
        SetActions(newState)
    }
    const AddCommand = () =>{
        const newState = [
            ...actions,{
                type:'command',
                id: actions.length + 1
            }
        ]
        SetActions(newState)
    }
    const AddService = () =>{
        
        const newState = [
            ...actions,{
                type:'service',
                id: actions.length + 1
            }
        ]
        SetActions(newState)
        {/* 
            fetch('http://localhost:8000/Actions',{
            method: 'POST',
            headers:{"Content-type": "application/json"},
            body: JSON.stringify(action)
        })
    */}
    }
    const iconsAction = [
        {icon : <SettingsSharp />, name: 'Service', action: AddService},
        {icon : <Code />, name: 'Commande', action: AddCommand},
        {icon : <Description />, name: 'Script', action: AddScript},
        {icon : <Storage />, name: 'Base de donnÃ©es', action: AddDB}
    ]
    const handleClose=() =>{
        setOpen(false)
    }

    const handleOpen=() =>{
        setOpen(true)
    }
    return ( 
        <Container>
            <Card className= {classes.root}>
                <CardHeader 
                title = {name}
                subheader = 'sequence'
                action = {
                    <SpeedDial
                    ariaLabel="SpeedDial"
                    icon={<SpeedDialIcon />}
                    open={ open }
                    direction = 'left'
                    onOpen={handleOpen}
                    onMouseLeave={handleClose}
                >
                    {iconsAction.map((action) => (
                        <SpeedDialAction 
                            key={action.name}
                            icon = {action.icon}
                            tooltipTitle={action.name}
                            tooltipPlacement = 'top'
                            onClick= {action.action}
                        />
                    ))}

                </SpeedDial>
                }
                />

                <CardContent>
                        {actions.map(item => (
                            (
                                item.type === "service" ? <Service key={item.id} /> :
                                item.type === "script" ?<ARScript key={item.id} />:
                                item.type === "database " ? <Database key={item.id} /> :
                                item.type === "command " ? <Command key={item.id} /> :
                                null
                            )
                            
                        ))}

                </CardContent>
            </Card>
        </Container>
     );
}
 
export default ARCard;
