import { Box, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,  FormControl, IconButton, InputAdornment, InputLabel, Menu, MenuItem, Paper, Select, TextField } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import {  AccountCircle, Comment, Delete, FileCopy,  MoreVert, Web } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1)

    },
    fields:{
        minWidth:300,
        marginRight:theme.spacing(2)
    },
    dropDown:{
        minWidth:100,
        width:200
    }
}))
const WebAction = ({actionID,index}) => {
    const classes = useStyles()
    const [anchorEl, setAnchor] = useState(null)
    const { deleteAction, duplicateAction,saveData} = useContext(ActionContext)
    const [state, setState] = useState([])
    const [actionType, setActionType] = useState("Authentification")
    const [openDialog, setOpenDialog] = useState(false)

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }
    const reduire = () =>{

        setState({ ...state, id:actionID, index:index})

        //console.log(state,actionID)
    }

    const testConnection = async (e) =>{
        if(e.target.value !== ''){

            console.log('accessing ',e.target.value)

            await fetch(`http://localhost:5000/api/PARPRE/link?url=${e.target.value}`)
                .then(res => res.json())
                .then(result => console.log(result.result))
        }
  
    }
    const showDialog = () =>{
        setOpenDialog(true);
    }

    const closeDialog = () =>{
        setOpenDialog(false);
    }

    const handleDialogSubmit =( ) =>{

    }

    return ( 
        <div>
            <Paper 
                elevation={0}
                className= {classes.root}>
                    <Box 
                display='flex'
                >
                    <Box my="auto" mx={2} >
                        <Web color="primary" />

                    </Box>
                    <Box my="auto" flexGrow={0.5}>
                        <TextField
                        
                        className={classes.fields} 
                        id='url'
                        
                        color='primary'
                        label= 'Lien'
            
                        onChange={(e) => setState({...state, url:e.target.value})}
                        onBlur={(e) => (testConnection(e))}
                        />
                    </Box>
                    <Box flexGrow={0.6} >
                        <FormControl className={classes.dropDown}>
                            <InputLabel>Etat</InputLabel>
                            <Select value={actionType} onChange={(e) => (setActionType(e.target.value))}>
                                <MenuItem value="Authentification" >Autentification</MenuItem>
                                <MenuItem value="Navigation">Navigation</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>
                    
                    <Box my="auto">
                        <FormControl >
                            <Button variant="contained" color="primary" onClick={showDialog}>options</Button>
                        </FormControl>
                    </Box>
                    <Dialog
                    open={openDialog}
                    onClose={closeDialog}
                    fullWidth={200}
                    onBlur={() => {saveData(
                        {
                            index:index,
                            type:"webAction",
                            url:state.url,                             
                            type:"webAction", 
                            informations:
                            {
                                login : state.login,
                                loginSelector : state.loginSelector, 
                                password : state.password, 
                                passwordSelector : state.passwordSelector
                            }
                        })}
                    }
                    >
                        <DialogTitle id='dialog-title' >Informations </DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                Le sélecteur est obtenu en faisant un click droit sur le champ à automatiser,  
                                Inspecter" puis click droit sur l'élément html et "copy selector"
                            </DialogContentText>
                            <div>
                                <Box display="flex" justifyContent="space-around">
                                    <TextField
                                    value={state.login}
                                    variant="outlined"
                                    autoFocus
                                    margin="dense"
                                    id="login"
                                    label="Login"
                                    type="email"
                                    onChange={(e) => (setState({...state, login:e.target.value}))}
                                    inputProps={{startAdornment : (<InputAdornment position="start"> <AccountCircle />test</InputAdornment> ),}}
                                    />
                                    <TextField
                                    value={state.loginSelector}
                                    variant="outlined"                                    
                                    margin="dense"
                                    id="login-selector"
                                    label="sélecteur"
                                    onChange={(e) => (setState({...state, loginSelector:e.target.value}))}
                                    />
                                </Box>

                                <Box display="flex" justifyContent="space-around">
                                    <TextField
                                    variant="outlined" 
                                                                      
                                    margin="dense"
                                    id="password"
                                    label="Mot de passe"
                                    type="password"
                                    onChange={(e) => (setState({...state, password:e.target.value}))}
                                    />
                                    <TextField
                                    value={state.passwordSelector}
                                    variant="outlined"
                                    margin="dense"
                                    id="password-selector"
                                    label="sélecteur"
                                    onChange={(e) => (setState({...state, passwordSelector:e.target.value}))}

                                    />
                                </Box>

                                <Box display="flex" justifyContent="space-around">
                                    <FormControl className={classes.dropDown}>
                                        <InputLabel>Navigateur</InputLabel>
                                        <Select value="Chrome">
                                            <MenuItem value="Chrome" >Chrome</MenuItem>
                                            <MenuItem value="Firefox">Firefox</MenuItem>
                                            <MenuItem value="Edge">Edge</MenuItem>
                                        </Select>
                                    </FormControl>

                                    <FormControl className={classes.dropDown}>
                                        <InputLabel>Mode de Navigation</InputLabel>
                                        <Select value="Normal" onChange={(e) => (setState({...state, navigateur:e.target.value}))}>
                                            <MenuItem value="Normal" >Normal</MenuItem>
                                            <MenuItem value="Privé">privé</MenuItem>
                                            
                                        </Select>
                                    </FormControl>

                                    
                                    
                                </Box>
                                
                            </div>

                        </DialogContent>
                        <DialogActions>
                            <Button onclick = {() => (console.log("state ",state))}>
                                Confirmer
                            </Button>
                            
                        </DialogActions>

                    </Dialog>
                    
                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(actionID)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={() => {saveData(
                            {
                                index:index, type:"lien", url : state.link, status : actionType
                            })
                            ;setAnchor(null)}} ><FileCopy /> save</MenuItem>
                        <MenuItem  ><Comment /> Réduire</MenuItem>
                    </Menu>
                    <IconButton onClick={handleClick}>
                        <MoreVert />

                    </IconButton>
                </Box>
            </Paper>
        </div>
     );
}
 
export default WebAction;
