import { Avatar, Box, Grid, IconButton, Menu, MenuItem, Paper, TextField, Typography } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Delete, FileCopy, MoreVert, Description } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"

const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(1),
        marginBottom: theme.spacing(1)

    },
    fields:{
        width:"100%" 
    },
    smallAvatar: {
        width: theme.spacing(2.5),
        height: theme.spacing(2.5)
      }
}))
const ARScript = ({index}) => {
    const classes = useStyles()
    const { deleteAction, duplicateAction,saveData,AddServer} = useContext(ActionContext)

    const [anchorEl, setAnchor] = useState(null)
    const [state, setState] = useState({})

    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }

    //context pour sauvegarder l'état dans le parent

    const saveInformations = () =>{
        if(state.service ===undefined || state.server === undefined){return}
        saveData(
            {
                index:index, type:"service", server : state.server , name : state.service
            }
        )
    }
    return ( 
        <div>
            <Paper 
            elevation={0}
            className= {classes.root}>
                <Grid 
                container
                alignItems="center"
                spacing={2}

                >

                    <Grid  item sm={1} md={1} xl={1}>
                        <Box my="auto" display="flex" justifyContent="center">
                            <Description color="primary" />
                        </Box>
                    </Grid>
                    

                    <Grid  item sm={2} md={2} xl={2}>
                        <TextField
                        onChange={(e) => setState({...state, server:e.target.value})}
                        className={classes.fields} 
                        id='server'
                        color='primary'
                        label= 'Serveur' />
                    </Grid>
                    
                    <Grid  item sm={8} md={8} xl={8}>
                        <TextField 
                        onChange={(e) => setState({...state, path:e.target.value})}
                        className={classes.fields} 
                        id='Path'
                        color='primary'
                        label= 'Path' />
                    </Grid>

                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(index)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={handleClick} ><FileCopy /> Dupliquer</MenuItem>
                    </Menu>
                    
                    <Grid item md={1} xl={1}  >
                        <Grid container spacing={3} alignItems="center" >
                            <Grid item md={6} >
                            <IconButton onClick={handleClick}>
                            <MoreVert  />
                        </IconButton>
                            </Grid>
                        
                            <Grid item md={6}>
                                <Box my="auto" >
                                    <Avatar className={classes.smallAvatar} >
                                    
                                    <Typography>
                                        {index + 1}
                                    </Typography> 
                                    
                                    
                                    </Avatar>

                                </Box>
                            </Grid>
                        
                    </Grid>
                        </Grid>
                       
                    
                </Grid>
            </Paper>
        </div>
     );
}
 
export default ARScript;
