import {Avatar, Button, Card, makeStyles, Paper, Typography} from '@material-ui/core'
import { useEffect, useState } from 'react';
import { CallMissedSharp, Code, Description, Http, SettingsSharp, Storage, Web } from '@material-ui/icons';

const useStyles = makeStyles((theme)=>({
    root:{
        

    },
    fields:{
        padding:theme.spacing(2),
        margin:theme.spacing(2),
        display:'flex',
        justifyContent:'space-between'
    },
    
}))


const Manage = () => {
    
    const [POS, SetPOS] = useState([])

    useEffect(()=>{
        getPos()
    },POS)


    const classes = useStyles()

    const getPos = async () =>{
        await fetch(`http://localhost:5000/api/POS`)
            .then(Response =>Response.json())
            .then(result=>{
                console.log(result)
                SetPOS(result)
            })
            
    }
    return ( 
        <div>
            <Typography>Toutes les Procedures d'Ouverture de Service (POS)</Typography>
                        
            <div>
                {POS.map(pos => (
                    //<Card >
                        <Paper className= {classes.fields} elevation={0.5}> 
                            <Avatar>{pos.name[0]}</Avatar>
                            <Typography> {pos.name}</Typography>
                            <Typography> {pos.Arret.length}</Typography>
                            <Typography> {pos.auteur}</Typography>
                            <Typography> {pos.date_de_creation}</Typography>
                            <Typography> {pos._id}</Typography>
                        </Paper>
                   //x</Card>
                    
                    
                ))}
            </div>
            
        </div>
     );
}
 
export default Manage;
