import { Avatar, Box, FormControl, IconButton, InputLabel, Menu, MenuItem, Paper, Select, TextField, Typography } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Delete, FileCopy, MoreVert, Storage } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"


const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1)

    },
    fields:{
        marginRight:theme.spacing(2),
        minWidth:theme.spacing(12)

    },
    smallAvatar: {
        width: theme.spacing(2.5),
        height: theme.spacing(2.5)
      }
}))
const Database = ({index,type}) => {
    const classes = useStyles()
    const [anchorEl, setAnchor] = useState(null)
    const { deleteAction} = useContext(ActionContext)
    const [state, setState] = useState({})
    const [status, setStatus] = useState(type)


    const handleClick = (event) =>{
        setAnchor(event.currentTarget)
    }
    const handleClose= () => {
        setAnchor(null)
    }
    return ( 
        <div>
            <Paper 
            elevation={0}
            className= {classes.root}>
                <Box 
                display='flex'
                justifyContent='space-between'

                >
                    <Box my="auto">
                        <Storage color="primary" />

                    </Box>
                    <div>
                        <TextField
                        className={classes.fields} 
                        id='server'
                        color='primary'
                        label= 'Serveur' 
                        onChange={(e) => setState({...state, server:e.target.value})}

                        />
                    </div>
                    <div>
                    <FormControl className={classes.fields}>
                        <InputLabel>Action</InputLabel>
                        <Select value={status} onChange={(e) => setStatus(e.target.value)} >
                            <MenuItem value="stop">Stop</MenuItem>
                            <MenuItem value="start">Start</MenuItem>
                            <MenuItem value="status" >Status</MenuItem>
                            
                        </Select>
                    </FormControl>
                    </div>
                    <div>
                        <TextField 
                        autoComplete="false"
                        className={classes.fields} 
                        id='DBName'
                        color='primary'
                        label= 'Instance'
                        onChange={(e) => setState({...state, Database:e.target.value})}
                        />
                    </div>
                    <div>
                        <TextField 
                        className={classes.fields} 
                        onChange={(e) => setState({...state, action:e.target.value})}
                        id='Action'
                        color='secondary'
                        label= 'Action'
                        placeholder='STOP'
                         />
                    </div>

                    <Menu
                        id="menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => deleteAction(index)} ><Delete />Supprimer</MenuItem>
                        <MenuItem onClick={handleClick} ><FileCopy /> Dupliquer</MenuItem>
                    </Menu>
                    <IconButton onClick={handleClick}>
                        <MoreVert />

                    </IconButton>
                    <Box my="auto" >
                        <Avatar className={classes.smallAvatar} >
                        
                        <Typography>
                            {index + 1}
                        </Typography> 
                        
                        </Avatar>

                    </Box>
                    
                </Box>
            </Paper>
        </div>
     );
}
 
export default Database;
