import { Avatar, Box, FormControl, Grid, IconButton, InputLabel, Menu, MenuItem, Paper, Select, TextField, Typography } from "@material-ui/core"
import {makeStyles} from '@material-ui/core'
import { Delete, FileCopy, MoreVert, Storage } from "@material-ui/icons"
import { useContext, useState } from "react"
import ActionContext from "../../Context/ActionContext"
import OptionMenu from "../Creation/OptionMenu"


const useStyles = makeStyles((theme)=>({
    root:{
        background:'#F6F6FB',
        padding: theme.spacing(1),
        marginBottom: theme.spacing(1)

    },
    fields:{
        width:"100%"

    },
    smallAvatar: {
        width: theme.spacing(2.5),
        height: theme.spacing(2.5)
      }
}))

const Database = ({index,type}) => {
    const classes = useStyles()
    const { deleteAction, duplicateAction,saveData,AddServer} = useContext(ActionContext)

    const [state, setState] = useState({})
    const [status, setStatus] = useState(type)
    const [databaseType, setDatabaseType] = useState("MSSQL")
    const [OS, SetOS] = useState("")



    const saveInformations = () =>{

        if(state.database ===undefined || state.server === undefined){return}

        saveData(
            {
                index:index, type:"database", server : state.server , name : state.database,action : status,databaseType : databaseType
            }
        )
    }
    return ( 
        <div>
            <Paper 
            elevation={0}
            className= {classes.root}>
                <Grid 
                    container 
                    spacing={2}
                    alignItems="center"

                >
                    <Grid item md={1} xl={1}  >
                        <Box  display="flex" justifyContent="center">
                            <Storage color="primary" />
                        </Box>
                        
                    </Grid>
                    
                    <Grid item xs={2} md={2} xl={2}>
                        <TextField
                        className={classes.fields} 
                        id='server'
                        color='primary'
                        label= 'Serveur' 
                        onChange={(e) => setState({...state, server:e.target.value})}
                        onBlur={saveInformations}

                        />
                    </Grid>
                    <Grid item xs={2} md={2} xl={2}>
                    <FormControl className={classes.fields}>
                        <InputLabel>Action</InputLabel>
                        <Select value={status} onChange={(e) => setStatus(e.target.value)} onBlur={saveInformations} >
                            <MenuItem value="stop">Stop</MenuItem>
                            <MenuItem value="start">Start</MenuItem>
                            <MenuItem value="status" >Status</MenuItem>                            
                        </Select>
                    </FormControl>
                    </Grid>
                    <Grid item xs={3} md={3} xl={3}>
                        <TextField 
                        autoComplete="false"
                        className={classes.fields} 
                        id='DBName'
                        color='primary'
                        label= 'Instance'
                        onChange={(e) => setState({...state, database:e.target.value})}
                        onBlur={saveInformations}

                        />
                    </Grid>
                    <Grid item xs={3} md={3} xl={3}>
                    <FormControl className={classes.fields}>
                        <InputLabel>Type</InputLabel>
                        <Select value={databaseType} onChange={(e) => setDatabaseType(e.target.value)} onBlur={saveInformations} >
                            <MenuItem value="MSSQL">MSSQL</MenuItem>
                            <MenuItem value="MONGODB">MONGODB</MenuItem>
                            <MenuItem value="Oracle" >Oracle</MenuItem>                            
                        </Select>
                    </FormControl>
                    </Grid>

                    
                    <Grid item md={1} xl={1}  >
                        <Grid container spacing={3} alignItems="center" >
                            <Grid item md={6} >
                                <IconButton >
                                    <OptionMenu index={index} deleteAction={deleteAction} duplicateAction={duplicateAction} />

                                </IconButton>
                            </Grid>
                        
                            <Grid item md={6}>
                                <Box my="auto" >
                                    <Avatar className={classes.smallAvatar} >
                                    
                                    <Typography>
                                        {index + 1}
                                    </Typography> 
                                    
                                    
                                    </Avatar>

                                </Box>
                            </Grid>
                        
                    </Grid>
                        </Grid>
                       
                    
                </Grid>
            </Paper>
        </div>
     );
}
 
export default Database;
