import { Avatar, Box, Divider, Drawer, Grid, LinearProgress, makeStyles, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from "@material-ui/core";
import { Computer, Rowing, Settings } from "@material-ui/icons";
import { useEffect, useState } from "react";

const drawerWidthRight = 280

const useStyles = makeStyles((theme) =>{

    return{
        root:{
            
        },
        navRight:{
            width: drawerWidthRight,
            //display:'none'
        },
        drawerPaperRight:{
            width : drawerWidthRight,
            backgroundColor:"#FFFFF",
            borderLeft:'0px'

        }

}})



  

const RightNav = ({ServerRow}) => {
    const [progress, setProgress] = useState(0);

    /*
    useEffect(() => {
      const timer = setInterval(() => {
        setProgress((prevProgress) => (prevProgress >= 100 ? 0 : prevProgress + 20));
      }, 400);
  
      return () => {
        clearInterval(timer);
      };
    }, []);
    */

    const classes = useStyles()
    return ( 
        <div>
            
            <Drawer
            elevation={0}
            variant = "permanent"
            anchor = 'right'
            classes = {{paper : classes.drawerPaperRight}}
            className={classes.navRight}
            >                
                <Box>
                    <Box m={2} display="flex" justifyContent="center">
                        <Typography gutterBottom>
                            Informations                                      
                        </Typography>
                    </Box>
                    
                    <Box>
                        <TableContainer>
                            <Table size="small">
                                <TableHead  >
                                    <TableRow >
                                        <TableCell size="small">Prod</TableCell>
                                        <TableCell size="small">h-Prod</TableCell>
                                        <TableCell size="small">DEV</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                {ServerRow.map((row) => (
                                    <TableRow key={row.name}>
                                    <TableCell component="th" scope="row" size="small">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="left" size="small">{row.hprod}</TableCell>
                                    <TableCell align="left" size="small">{row.dev}</TableCell>
                                    </TableRow>
                                ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                </Box>

                <Box>
                    <Box m={2} display="flex" justifyContent="center">
                        <Typography gutterBottom>
                            VÃ©rification Active                                      
                        </Typography>
                    </Box>
                    
                    <Grid container spacing={1} alignItems="center" >
                        <Grid item md={2} >
                            <Box display="flex" justifyContent="center">
                                <Computer style={{color: "gray"}} />
                            </Box>
                            
                        </Grid>
                        
                        <Grid item md={10}>                       
                            <Typography variant="subtitle1" display="block"> Ping : SW11203</Typography>
                            <Grid container direction="row" alignItems="center" >
                                <Grid item md={5}>
                                    <Typography variant="caption">En cours</Typography>
                                </Grid>
                                <Grid item md={7}>
                                <LinearProgress variant="determinate" value={progress}  />
                                </Grid>                            
                            </Grid>
                        </Grid>
                    </Grid>
                    <Divider style={{margin:"10px "}}/>
                    <Grid container spacing={1} alignItems="center" >
                        <Grid item md={2} >
                            <Box display="flex" justifyContent="center" >
                                <Avatar style={{padding:"auto"}} variant="circular">
                                    <Settings style={{color: "gray"}} />
                                </Avatar>
                                
                            </Box>
                            
                        </Grid>
                        
                        <Grid item md={10}>                       
                        <Typography variant="subtitle1" display="block"> Service : BITS</Typography>
                            <Grid container direction="row" alignItems="center" >
                                <Grid item md={5}>
                                    <Typography variant="caption">En cours</Typography>
                                </Grid>
                                <Grid item md={7}>
                                <LinearProgress variant="determinate" value={progress}  />
                                </Grid>                            
                            </Grid>
                        </Grid>
                    </Grid>

                </Box>
            </Drawer>
        
        </div> 
     );
}
 
export default RightNav
