import { Button, Card, CardContent, CardHeader,  makeStyles, Typography} from '@material-ui/core'
import { Add, Code, Description, SettingsSharp, Storage } from '@material-ui/icons';
import Service from './Actions/Service';
import ARScript from './Actions/ARScript';
import { useEffect, useState } from 'react';
import { useHistory } from 'react-router';

const cardWidth = 950
const useStyles = makeStyles(theme =>({
    root:{
        width : cardWidth,
        marginBottom : theme.spacing(2) 
    }
}))
const ARCard = ({name}) => {
    const classes = useStyles();
    const [actions, SetActions] = useState([])
    const history = useHistory()
    useEffect(() =>{
        fetch("http://localhost:8000/Actions")
        .then(res => res.json())
        .then(data => SetActions(data))
    },[])

    const handleClick = (action) =>{

        const newState = [
            ...actions,action
        ]
        SetActions(newState)
        {/* 
            fetch('http://localhost:8000/Actions',{
            method: 'POST',
            headers:{"Content-type": "application/json"},
            body: JSON.stringify(action)
        })
    */}
    }
    const iconsAction = [
        {icon : <SettingsSharp />, name: 'Service'},
        {icon : <Code />, name: 'Commande'},
        {icon : <Description />, name: 'Script'},
        {icon : <Storage />, name: 'Base de données'}
    ]
    return ( 
        <div>
            <Card className= {classes.root}>
                <CardHeader 
                title = {name}
                subheader = 'sequence'
                action = {
                    <Button 
                    color='primary' 
                    variant='contained'
                    disableElevation
                    onClick={() => (
                        
                        handleClick({type:"service",action:"stop"})
                    )}
                    >
                        <Add />
                        <Typography> Action</Typography>
                    </Button>
                }
                />
                <CardContent>
                        {actions.map(item => (
                            (item.type === "service" ? <Service key={item.id} /> : <ARScript />)
                        ))}

                </CardContent>
            </Card>
        </div>
     );
}
 
export default ARCard;
