import {Avatar, Button, Card, CardContent, Grid, makeStyles, Paper, Typography} from '@material-ui/core'
import { useEffect, useState } from 'react';
import { CallMissedSharp, Code, Description, Http, SettingsSharp, Storage, Web } from '@material-ui/icons';
import ListSSA from '../Components/Manage/ListSSA';

const useStyles = makeStyles((theme)=>({
    root:{
        margin:theme.spacing(1),
        flexGrow:theme.spacing(1)

    },
    fields:{
        padding:theme.spacing(2),
        display:'flex',
        justifyContent:'space-between'
    },
    
}))


const Manage = () => {
    
    const [POS, SetPOS] = useState([])

    useEffect(()=>{
        getPos()
    },POS)


    const classes = useStyles()

    const getPos = async () =>{
        await fetch(`http://localhost:5000/api/POS`)
            .then(Response =>Response.json())
            .then(result=>{
                console.log(result)
                SetPOS(result)
            })
            
    }
    return ( 
        <div>
            <Typography>Toutes les Procedures d'Ouverture de Service (POS)</Typography>
                        
            <div>
            <Grid container direction="row">
                    <Grid item xs={3} sm={1} >
                    <Typography>avatar</Typography>
                    </Grid>
                    <Grid item xs={3} sm={3}  >
                    <Typography> Nom</Typography>
                    </Grid>
                    <Grid item xs={3} sm={1}  >
                    <Typography> Nombre d'étapes</Typography>
                    </Grid>
                    <Grid item xs={3} sm={2}  >
                    <Typography> Auteur</Typography>
                    </Grid>
                    <Grid item xs={3} sm={2}  >
                    <Typography> Date de création </Typography>
                    </Grid>
                    <Grid item xs={3} sm={3} spacing={1} container justify="flex-end">
                        <Grid item>
                            <Button variant="outlined" color="default">PARPRE</Button>
                        </Grid>
                        <Grid item>
                            <Button variant="outlined" color="primary">POS</Button>
                        </Grid>
                    </Grid>
                    
                </Grid>
                {POS.map(pos => (                                      
                    <ListSSA ssa={pos} key={pos._id}/>
                ))}
            </div>
            
        </div>
     );
}
 
export default Manage;
