import { FormControl, FormControlLabel, FormGroup, FormLabel, Grid, makeStyles, Radio, RadioGroup, Switch, TextField, Typography } from "@material-ui/core"
import axios from "axios";
import { useEffect, useState } from "react";
import ARCard from "../Components/ARCard"
import RightNav from "../Components/Creation/RightNav";
import {useHistory} from 'react-router'

const drawerWidthRight = 260

const useStyles = makeStyles((theme) =>{
    return{
        root:{
            width:`calc(100% - ${drawerWidthRight}px)`   
        }

}})


const Create = () => {

    const [titleOpen, setTitleOpen] = useState(false)
    //titre de la parpre ou de la POS
    const [title, setTitle] = useState("APPXXXX_SSA")
    //les actions d'arrêt
    const [StopActions, setStopActions] = useState([])
    //les Actions de relance
    const [StartActions, setStartActions] = useState([])
    //creation automatique de la verification
    const [autoCreate, setAutoCreate] = useState(false)
    //Si on fait la relance automatiquement
    const [autoRelance, setAutoRelance] = useState(false)
    //Soit PARPRE ou POS
    const [documentType, setDocumentType] = useState("")
    //Le mode d'utilisation: creation ou modification
    const [pageMode, setPageMode] = useState("Création")

    const history = useHistory()

    //La page de création et la pae de modifiation sont les mêmes la difference est assurée par pageMode
    useEffect(()=>{

        document.title="Création de PARPRE / POS"
        
        if(history.location.state !== undefined){
            console.log("History ", history.location)
            setPageMode("Modification")
            setStopActions(history.location.state.Arret)
            setStartActions(history.location.state.Relance)
        }
        console.log("page mode : ",pageMode)
    })

    const [servers, setServers] = useState([
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" },
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" },
        { name: 'sw11203', hprod: 'sw11213', dev: "sw11003" }
    ])

    const saveServer = (server) =>{
       //console.log("Saving server ", server)
        setServers([...servers,{name:server}])
    }

    const classes = useStyles()

    const saveStart = (object) =>{
        setStartActions(object)

    }

    const saveStop = (object) =>{
        
        //let newServers = []

        //object.map(action=> newServers=[...newServers,{name : action.server}])

        //console.log("all servers ",newServers)

        setStopActions(object)

        //setServers(newServers)

    }

    const generateJson =  () => {
        console.log("name ",title)
        const parpre = {name : title ,auteur: "Samba NDIAYE", date_de_creation : new Date().toLocaleString(),type:"Parpre", Arret : StopActions, Relance:StartActions}

        axios.post(`http://localhost:5000/api/PARPRE/create`, parpre)
            .then(res => console.log(res))
            
        console.log(JSON.stringify(parpre))
    }

    return ( 
        

        <div className={classes.root}>   
            <Grid container spacing={2}>     
                <Grid item md={6}>
                { 
                    titleOpen ? 
                    (
                        <TextField 
                        placeholder="Changer le titre"
                        onBlur={() => setTitleOpen(false)}
                        margin="dense"
                        onChange={(e) => setTitle(e.target.value)}
                        autoFocus
                        value={title}
                        >
                            
                        </TextField>
                    ) 
                    : 
                    (
                        <div>
                            <Typography 
                            variant='h6'
                            gutterBottom
                            onClick={() => setTitleOpen(true)}
                            >
                                Nom : {title}

                            </Typography>
                        </div>
                    )
                }    
                </Grid>   

                

                {/**
                 <Grid item md={2}>
                  <FormGroup>
                        <FormControlLabel
                            control = {<Switch onChange={()=> setAutoCreate(!autoCreate)}/>}
                            checked={autoCreate}
                            label = "Verification-Auto"
                        >
                        

                        </FormControlLabel>
                        
                    </FormGroup>
                    </Grid> 
                 */}
               
                

                <Grid item md={3}>
                    <FormGroup>
                        <FormControlLabel
                            control = {<Switch onChange={()=> setAutoRelance(!autoRelance)}/>}
                            checked={autoRelance}
                            label = "Relance-Auto"
                        >
                        </FormControlLabel>
                        
                    </FormGroup>
                    
                </Grid>  
                <Grid item md={3}>
                    
                    <RadioGroup>
                        <FormGroup row>
                            <FormControlLabel value="POS" label="POS" control={<Radio/>} />
                            <FormControlLabel value="PARPRE" label="PARPRE" control={<Radio/>} />
                        </FormGroup>
                    </RadioGroup>                       
                   
                </Grid>  
                
            </Grid>
            
                <ARCard 
                    name="Arrêt" 
                    type="stop" 
                    actions={StopActions} 
                    SetActions={saveStop} 
                    autoRelance = {autoCreate}
                    AddServer={saveServer}
                    generateJson={generateJson}
                      
                />
                <ARCard 
                    name="Relance" 
                    type="start"
                    actions = {StartActions} 
                    SetActions = {saveStart} 
                    AddServer={saveServer} 

                />
                <RightNav ServerRow={servers}/>
        </div>
     );
}
 
export default Create;
