        flexGrow:theme.spacing(1)

    },
    fields:{
        padding:theme.spacing(2),
        display:'flex',
        justifyContent:'space-between'
    },
    
}))
const ListSSA = ({ssa}) => {
    const classes = useStyles()

    return ( 
        <div>
            <Card className={classes.root} elevation={0.5}>
                <CardContent  > 
