import { Box, Drawer, makeStyles, Typography } from "@material-ui/core";
import {DataGrid } from "@material-ui/data-grid";

const drawerWidthRight = 220

const useStyles = makeStyles((theme) =>{
    return{
        root:{
            
        },
        navRight:{
            width: drawerWidthRight,
            //display:'none'
        },
        drawerPaperRight:{
            width : drawerWidthRight,
            backgroundColor:"#FFFFF",
            borderLeft:'0px'

        },

}})
const ServerColumns = [
    { field: 'pro', headerName: 'Production', width: 20 },
    { field: 'IPP1', headerName: 'Validation', width: 10 },
    { field: 'IPPI2', headerName: 'Intégration', width: 10 }
  ];

  const columns = [
    { field: 'id', headerName: 'ID', width: 70 },
    { field: 'firstName', headerName: 'First name', width: 130 },
    { field: 'lastName', headerName: 'Last name', width: 130 },
    {
      field: 'age',
      headerName: 'Age',
      type: 'number',
      width: 90,
    },
    {
      field: 'fullName',
      headerName: 'Full name',
      description: 'This column has a value getter and is not sortable.',
      sortable: false,
      width: 160,
      valueGetter: (params) =>
        `${params.getValue('firstName') || ''} ${params.getValue('lastName') || ''}`,
    },
  ];
  
  const rows = [
    { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
    { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
    { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
    { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
    { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
    { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
    { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
    { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
    { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
  ];

const RightNav = () => {
    const classes = useStyles()
    return ( 
        <div>
            
            <Drawer
            elevation={0}
            variant = "permanent"
            anchor = 'right'
            classes = {{paper : classes.drawerPaperRight}}
            className={classes.navRight}
            >                
                <Box m={3} display="block">
                    <Typography gutterBottom>
                        Vérification active                                       
                    </Typography>
                    <Box>
                        <DataGrid columns={columns} rows={rows} />
                    </Box>
                </Box>
            </Drawer>
        
        </div> 
     );
}
 
export default RightNav
