import {Avatar, Button, Card, CardContent, Divider, Grid, IconButton, InputBase, makeStyles, Paper, Typography} from '@material-ui/core'
import { useEffect, useState } from 'react';
import { CallMissedSharp, Code, Description, Directions, Http, Menu, SearchSharp, SettingsSharp, Storage, Web } from '@material-ui/icons';
import ListSSA from '../Components/Manage/ListSSA';

const useStyles = makeStyles((theme)=>({
    root:{
        margin:theme.spacing(1),

    },
    fields:{
        padding:theme.spacing(2),
        display:'flex',
        justifyContent:'space-between'
    },
    
}))


const Manage = () => {
    
    const [POS, SetPOS] = useState([])

    useEffect(()=>{
        getPos()
    },POS)


    const classes = useStyles()

    const getPos = async () =>{
        await fetch(`http://localhost:5000/api/POS`)
            .then(Response =>Response.json())
            .then(result=>{
                console.log(result)
                SetPOS(result)
            })
            
    }
    return ( 
        <div>
            <Typography>Toutes les Procedures d'Ouverture de Service (POS)</Typography>
            <div>
                <Grid container spacing={1}>
                    <Grid item sm={4}>
                         <Button variant="contained"> Nouveau</Button>
                    </Grid>
                    <Grid item sm={4}>
                    <Paper component="form" >
                        <IconButton aria-label="menu">
                            <Menu />
                        </IconButton>
                        <InputBase
                            placeholder="Search Google Maps"
                            inputProps={{ 'aria-label': 'search google maps' }}
                        />
                        <IconButton type="submit"  aria-label="search">
                            <SearchSharp />
                        </IconButton>                        
                    </Paper>
                    </Grid>
                </Grid>
            </div>

            <div>
            <Grid container direction="row" className={classes.root}>
                    <Grid item xs={3} sm={1} >
                    <Typography>Avatar</Typography>
                    </Grid>
                    <Grid item xs={3} sm={3}  >
                    <Typography> Nom</Typography>
                    </Grid>
                    <Grid item xs={3} sm={1}  >
                    <Typography> étapes</Typography>
                    </Grid>
                    <Grid item xs={3} sm={2}  >
                    <Typography> Auteur</Typography>
                    </Grid>
                    <Grid item xs={3} sm={2}  >
                    <Typography> Date de création </Typography>
                    </Grid>
                    <Grid item xs={3} sm={3} spacing={1} container justify="center">
                        <Typography>Actions</Typography>
                    </Grid>
                    
                </Grid>
                {POS.map(pos => (                                      
                    <ListSSA ssa={pos} key={pos._id}/>
                ))}
            </div>
            
        </div>
     );
}
 
export default Manage;
